import './TodoApp.css'
import { useState } from 'react'
import {BrowserRouter, Routes, Route} from 'react-router-dom'
export default function TodoApp(){
    return (
        <div className="TodoApp">
            {/* Todo Management Application  */}
            <BrowserRouter>
                <Routes>
                    <Route path='/login' element = {<LoginComponent/>}></Route>
                    <Route path='/welcome' element = {<WelcomeComponent/>}></Route>
                </Routes>
            </BrowserRouter>
            {/* <LoginComponent/>
            <WelcomeComponent/> */}
        </div>
    )
}

function LoginComponent() {
    const [username, setUsername] = useState('in28minutes')
    const [password, setPassword] = useState('dummy')
    const [showSuccessMessage, setshowSuccessMessage] = useState(false)
    const [showErrorMessage, setshowErrorMessage] = useState(false)
    function handleUsernameChange(event){
        // console.log(event.target.value);
        setUsername(event.target.value)
    }
    function handlePasswordChange(event){
        // console.log(event.target.value);
        setPassword(event.target.value)
    }
    function handleSubmit(){
        console.log(username);
        console.log(password);
        if (username ==='in28minutes' && password ==='dummy'){
            console.log('Success')
            setshowSuccessMessage(true)
            setshowErrorMessage(false)
        } else{
            console.log('Fail')
            setshowSuccessMessage(false)
            setshowErrorMessage(true)
        }

    }

    // function SuccessMessageComponent(){
    //     if (showSuccessMessage)
    //     {
    //         return (
    //             <div className='successMessage'>Authentication Successfull</div>
    //         )
    //     }

    //     return null 
    // }


    // function FailureMessageComponent(){
    //     if (showErrorMessage)
    //     {
    //         return (
    //             <div className='errorMessage'>Authentication Failed. Please try again</div>
    //         )
    //     }

    //     return null 
    // }

    return (
        <div className="Login">
            {/* <SuccessMessageComponent/>
            <FailureMessageComponent/> */}
            {showSuccessMessage && <div className='successMessage'>Authentication Successfull</div>}
            {showErrorMessage && <div className='errorMessage'>Authentication Failed</div>}
            <div className="LoginForm">
                <div>
                    <label>User Name</label>
                    <input type="text" name ="username" value={username} onChange = {handleUsernameChange}/>
                </div>
                <div>
                    <label>Password</label>
                    <input type="password" name ="password" value={password} onChange = {handlePasswordChange} />
                </div>
                <div>
                <button type="button" name ="login" onClick={handleSubmit}>login </button>
                </div>
            </div>
        </div>
    )
}



// function SuccessMessageComponent(){
//     return (
//         <div className='successMessage'>Authentication Successfull</div>
//     )
// }

function WelcomeComponent() {
    return (
        <div className="Welcome">
            Welcome Component
        </div>
    )
}
